import React from 'react';
import { connect } from 'react-redux';

//import Grid from './Grid';
import '../css/square.css';
import { selectSquare } from './../actions';

class Square extends React.Component {

    onCellClick(phase) {
        if (phase === 'RESOURCE_PHASE') {
            console.log(this.props.index);
            this.props.selectSquare(this.props.index)
        }
    }



    render() {
        let cellCalss = "cell";
        if (this.props.selectSquare === this.props.index) {cellCalss += " selectedCell" }

        return (
            <div className={cellCalss} onClick={() => {this.onCellClick(this.props.phase)}}>
                {this.props.cell.ocupante}
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        cell: state.board[ownProps.index],
        selectSquare: state.board.selectSquare,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { selectSquare })(Square);
