import React from 'react';
import { connect } from 'react-redux';

import { selectResource, changePhaseTo, changePlayerTo } from '../actions';


class Resources extends React.Component {
    //During PHASE_CHOOSING_RESOURCE If you the first player you can select one of 5 resources
    //if you are the second - get random resource.
    resourceSelection = resource => {
        if (this.props.phase === "PHASE_CHOOSING_RESOURCE") {
            if (resource !== "?") {
                this.props.selectResource(resource);
            } else {
                //TODO change to card random
                this.props.selectResource("random resource");
            }

        }
    }

    resourcePlacing = () => {
        this.props.changePhaseTo("PHASE_PLACING_RESOURCE");
        const nextFirstPlayer = this.props.firstPlayer === "player" ? "random" : "player";
        this.props.changePlayerTo(nextFirstPlayer);

    }

    render() {
        //TODO - button active depending of players order 

        return (
            <div>
                {(() => {
                    if (this.props.firstPlayer === "player") {
                        return (
                            <div>Select resource:
                                <button onClick={() => { this.resourceSelection("Wood") }}>Wood</button>
                                <button onClick={() => { this.resourceSelection("Wheat") }}>Wheat</button>
                                <button onClick={() => { this.resourceSelection("Brick") }}>Brick</button>
                                <button onClick={() => { this.resourceSelection("Glass") }}>Glass</button>
                                <button onClick={() => { this.resourceSelection("Stone") }}>Stone</button>
                            </div>
                        );
                    } else {
                        return <button onClick={() => { this.resourceSelection("?") }}>Get random resource</button>
                    }
                })()}

                Selected:
                <button onClick={() => { this.resourcePlacing() }}>
                    {this.props.selectedResource}
                </button>

            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        selectedResource: state.selectedResource,
        phase: state.phase,
        firstPlayer: state.firstPlayer
    }
};

export default connect(mapStateToProps, { selectResource, changePhaseTo, changePlayerTo })(Resources);
