import React from 'react';
import ReactDOM from 'react-dom';
import { connect } from 'react-redux';
import ResourceBtn from './../ResourceBtn';

import { changeResourceToWild, clearSelection } from './../../actions';
import { ALL_RESOURCES } from './../constants';


const Modal = props => {
    let isShowModal = true;
    
    if(isShowModal) {
    return ReactDOM.createPortal(
        <div className=" modal">
            <div className=" modal-body ">
                <div className="header">Ð¡hoose a resource to turn into a wild </div>
                <div className="content">
                    <p> {
                        ALL_RESOURCES.map(
                            resource => <ResourceBtn key={resource.title} resource={resource} />
                        )
                    }
                    </p>
                </div>
                <div className="actions">
                    <button
                        onClick={() => { 
                            props.qwerty();

                            props.changeResourceToWild();

                            props.clearSelection();
                         
                             }}
                    >Confirm changing to WILD</button>
                </div>
            </div>
        </div >,
        document.querySelector('.flexRow')

    )} else {return null}


};

export default connect(null, { changeResourceToWild, clearSelection })(Modal);
