import { messageSelectResource } from './../components/messages';

//phases
export const RESOURCE_PHASE = "RESOURCE_PHASE";
export const BUILDING_PHASE = "BUILDING_PHASE";
export const SCORING_PHASE = "SCORING_PHASE";

//square's occupants
export const EMPTY_SQUARE = "EMPTY";
export const WOOD = "WOOD";
export const STONE = "STONE";
export const BRICK = "BRICK";
export const WHEAT = "WHEAT";
export const GLASS = "GLASS";
export const RESOURCE = [WOOD, STONE, BRICK, WHEAT, GLASS];

const INITIAL_BOARD = {
    indexes: [
        11,
        12,
        13,
        14,
        21,
        22,
        23,
        24,
        31,
        32,
        33,
        34,
        41,
        42,
        43,
        44
    ]
}

INITIAL_BOARD.indexes.forEach(index => {
    INITIAL_BOARD[index] = { occupant: "WOOD" };
});
INITIAL_BOARD[{ x: 1, y: 1 }] = { occupant: "2" };
//  11: "BLOCK",
///  12: "wheat",

export const INITIAL_STATE = {
    phase: RESOURCE_PHASE,
    // phase: BUILDING_PHASE,
    selectedPawn: null,
    selectedSquare: null,
    selectedPattern: [],
    buildings: [],
    board: INITIAL_BOARD,
    message: messageSelectResource()
};
