import React from 'react';
import { connect } from 'react-redux';

import { selectPawn, changePhaseTo, changeMessage } from '../actions';
import {
    message,
    RESOURCE_PHASE,
    WOOD,
    STONE,
    BRICK,
    WHEAT,
    GLASS,
} from './constants';
//import  { messagePlaceResource } from './messages';


class Resources extends React.Component {
    
    resourceSelection = resource => {
        if (this.props.phase === RESOURCE_PHASE) {
            this.props.selectPawn(resource);
            this.props.changeMessage(message.placeResource(resource.title));
        }
    }
    /*Select resource:
                <button onClick={() => { this.resourceSelection(WOOD) }}>Wood</button>
                <button onClick={() => { this.resourceSelection(WHEAT) }}>Wheat</button>
                <button onClick={() => { this.resourceSelection(BRICK) }}>Brick</button>
                <button onClick={() => { this.resourceSelection(GLASS) }}>Glass</button>
                <button onClick={() => { this.resourceSelection(STONE) }}>Stone</button>*/

    render() {
        return (
            <div>
                
                Card:
                <button onClick={() => { this.resourceSelection(this.props.resource) }}>{this.props.resource.title}</button>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        selectedPawn: state.selectedPawn,
        phase: state.phase,
        resource: state.resources[ownProps.id]
    }
};

export default connect(mapStateToProps, { selectPawn, changePhaseTo, changeMessage })(Resources);
