import React from 'react';
import { connect } from 'react-redux';

import { selectPawn, changePhaseTo, changeMessage } from '../actions';
import { message, RESOURCE_PHASE } from './constants';

class Resource extends React.Component {

    resourceSelection = resource => {
        if (this.props.phase === RESOURCE_PHASE) {
            this.props.selectPawn(resource);
            this.props.changeMessage(message.placeResource(resource.title));
        }
    }

    render() {
        if (!this.props.resource) { return null };
        return (
            <div>
                {this.props.resource.title}
                <button
                    onClick={() => { this.resourceSelection(this.props.resource) }}
                    className="wood resourceBtn"
                ></button>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        selectedPawn: state.selectedPawn,
        phase: state.phase,
        resource: state.resources[ownProps.id]
    }
};

export default connect(mapStateToProps, { selectPawn, changePhaseTo, changeMessage })(Resource);
