        return (
            <div className={this.cellClassName()} onClick={this.onCellClick()}>
                {this.props.cell.ocupante}
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        cell: state.board[ownProps.index],
        phase: state.phase
