import React, { useEffect } from 'react';
import { connect } from 'react-redux';

import { selectResource } from '../actions';


const Resources = () => {

  return (
    <div>
      Select resource:
      <button onClick={() => {console.log(this.props)}}>Wood</button>
      <button>Wheat</button>
      <button>Brick</button>
      <button>Glass</button>
      <button>Stone</button>
      <button>?</button>
      Selected: {this.props.selectResource}

    </div>
  );
}

const mapStateToProps = (state) => {
    return {
        selectedResource: state.selectedResource
    }
};

export default connect(null, { selectResource })(Resources);
