import React from 'react';
import { connect } from 'react-redux';

import { fetchBuilding } from '../../actions';
import data from './buildings.json';


class Building extends React.Component {

    selectBuilding ()  {
        
        this.props.fetchBuilding(data.Well);
    }
   componentDidMount() {
    this.props.fetchBuilding(data[this.props.title]);
   }

    render() {
        return (
            <div>
                <button onClick={() => this.selectBuilding()}>{this.props.title}</button>
            </div>
        );
    }

};

const mapStateToProps = (state) => {
    return {
        selectedPawn: state.selectedPawn,
        selectedSquare: state.selectedSquare,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { fetchBuilding })(Building);
