import React from 'react';
import { connect } from 'react-redux';

import { fetchBuilding, selectPawn, changeMessage } from '../../actions';
import data from './buildings.json';
import { message, BUILDING_PHASE, SCORING_PHASE } from './../constants';
import { calcScore } from './scoring';



class Building extends React.Component {

    testMe(pattern) {

        //[[12,"WOOD"],...] -> [{x: "1", y: "2", resource: "WOOD"},...]
        const indexToPoint = arr => arr.map(square => {
            return {
                x: square[0].toString().charAt(0),
                y: square[0].toString().charAt(1),
                resource: square[1]
            }
        });

        // Move to 11, [{x: "1", y: "2", resource: "WOOD"},...] -> [[12,"WOOD"],...] and sort by index
        const pointToIndex = arr => {
            //move to x=1, y=1
            const dx = Math.min(...arr.map(point => Number(point.x))) - 1;
            const dy = Math.min(...arr.map(point => Number(point.y))) - 1;
            arr.forEach(point => {
                point.x -= dx;
                point.y -= dy;
            });
            //[{x: "1", y: "2", resource: "WOOD"},...] -> [[12,"WOOD"],...]
            return arr.map(point => {
                const index = point.x.toString() + point.y.toString();
                return [index, point.resource]
            //and sort by index
            }).sort((a, b) => { return a[0] - b[0] });


        }

        const rotate = arr => arr.map(point => { return { x: -point.y, y: point.x, resource: point.resource } });

        const mirrorX = arr => arr.map(point => { return { x: -point.x, y: point.y, resource: point.resource } });

        const mirrorY = arr => arr.map(point => { return { x: point.x, y: -point.y, resource: point.resource } });

        const rotated0pattern = indexToPoint(pattern);
        const rotated1pattern = rotate(pattern);
        const rotated2pattern = rotate(rotated1pattern);
        const rotated3pattern = rotate(rotated2pattern);


        const rez = [
            rotated0pattern,
            mirrorX(rotated0pattern),
            mirrorY(rotated0pattern),

            rotated1pattern,
            mirrorX(rotated1pattern),
            mirrorY(rotated1pattern),

            rotated2pattern,
            mirrorX(rotated2pattern),
            mirrorY(rotated2pattern),
        
            rotated3pattern,
            mirrorX(rotated3pattern),
            mirrorY(rotated3pattern)
        ].forEach(pointPattern => {
            console.log(pointPattern);
            pointToIndex(pointPattern)
        });


        console.log(rez);



    }

    selectBuilding() {
        if (this.props.phase === BUILDING_PHASE) {
            if (this.props.selectedPattern.length && "Pattern is OK") {
                this.props.selectPawn(this.props.building);

            }
            this.props.changeMessage(message.selectSquareForBuilding(this.props.building.title, this.props.selectedPattern.length));
        }
    }

    componentDidMount() {
        this.props.fetchBuilding(data[this.props.title]);
    }

    renderScoring() {
        if (this.props.phase === SCORING_PHASE) {
            return calcScore[this.props.building.title](this.props.board)
        };
    }

    render() {
        if (!this.props.building) {
            return null;
        } else {
            return (
                <div>
                    <button onClick={() => this.selectBuilding()}>{this.props.building.title}</button>
                    {this.renderScoring()}
                    <button onClick={() => this.testMe(this.props.building.pattern)}> testMe </button>
                </div>
            );
        }
    }

};

const mapStateToProps = (state, ownProps) => {
    return {
        phase: state.phase,
        building: state.buildings[ownProps.index],
        selectedPattern: state.selectedPattern,
        board: state.board
    }
};

export default connect(mapStateToProps, { fetchBuilding, selectPawn, changeMessage })(Building);
