import React from 'react';
import { connect } from 'react-redux';


import Resource from './Resource';
import { changePhaseTo, placeResource, clearSelection } from '../actions';

import {
    message,
    RESOURCE_PHASE,
    BUILDING_PHASE
} from './constants';
import '../css/resources.css';

const placeSelectedResource = () => {
    placeResource();
    clearSelection();
    changePhaseTo(BUILDING_PHASE);
;}

const renderActionHelper = (phase, selectedPawn, selectedSquare) => {
    if (phase === RESOURCE_PHASE) {
        if (!selectedPawn.type && !selectedSquare) {
            return `выбрать ячейку и ресурс`;
        } else if (!selectedPawn.type) {
            return `выбрать ресурс`;

        } else if (!selectedSquare) {
            return `выбрать ячейку`;
        } else {
            return <button onClick={() => placeSelectedResource()}> {message.placeResource(selectedPawn.title)} </button>
        }

    } else {
        return null;
    }
}

const ResourceSelector = props => {
    return (
        <div className="resourceSelector">
            Select resource
            <Resource id={0} />
            <Resource id={1} />
            <Resource id={2} />
            {renderActionHelper(props.phase, props.selectedPawn, props.selectedSquare)}
        </div>
    );
};

const mapStateToProps = state => {
    return {
        selectedPawn: state.selectedPawn,
        selectedSquare: state.selectedSquare,
        phase: state.phase
    }
};

export default connect(mapStateToProps, {})(ResourceSelector);




