import {
    SELECT_PAWN,
    SELECT_RESOURCE,
    SELECT_SQUARE,
    SELECT_PATTERN,
    CLEAR_SELECTION,
    PLACE_RESOURCE,
    PLACE_BUILDING,
    FETCH_BUILDING,
    CHANGE_PHASE
} from './types';

import {
    RESOURCE_PHASE,
    //  BUILDING_PHASE,
    //   FEEDING_PHASE,
    //  SCORING_PHASE
} from './../components/constants';


export const selectPawn = pawn => {
    return {
        type: SELECT_PAWN,
        payload: pawn
    };
}


export const selectResource = (resource, phase) => {
    if (phase === RESOURCE_PHASE) {
        return {
            type: SELECT_RESOURCE,
            payload: resource
        };
    }
    return { type: null };
}

export const selectSquare = (index) => {
    return {
        type: SELECT_SQUARE,
        payload: index
    };
}

export const selectPattern = (index) => {
    return {
        type: SELECT_PATTERN,
        payload: index
    };
}

export const clearSelection = () => {
    return {
        type: CLEAR_SELECTION
    };
}

export const placeResource = () => {
    return {
        type: PLACE_RESOURCE
    };
}

export const placeBuilding = () => {
    return {
        type: PLACE_BUILDING
    };
}

export const fetchBuilding = (building) => {
    return {
        type: FETCH_BUILDING,
        payload: building
    };
}

export const changePhaseTo = phase => {
    return {
        type: CHANGE_PHASE,
        payload: phase
    };
}
