import React from 'react';
import { connect } from 'react-redux';

//import { selectResource, changePhaseTo, placeResource, clearSelection } from '../actions';

const PatternDetector = ({ selectedPattern, board }) => {
    const showPattern = () => {
        console.log(selectedPattern);
        const preparedPattern = arr => {
            const rez = [];
            arr.forEach(element => {
                rez.push( {
                   x: element.toString().charAt(0),
                   y: element.toString().charAt(1),
                   resource: board[element]
                });
                // element.slice(0,1);
            });
            return rez;
        }
        console.log(preparedPattern(selectedPattern));
    }


    return (
        <div>
            <button onClick={() => showPattern()}>Show Pattern</button>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        selectedPattern: state.selectedPattern,
        board: state.board

    }
};

export default connect(mapStateToProps, {})(PatternDetector);
