import React from 'react';
import { connect } from 'react-redux';

//import Grid from './Grid';
import '../css/square.css';
import { selectSquare, selectPattern } from './../actions';

class Square extends React.Component {

    onCellClick(phase) {
        const filling = this.props.cell;

        if (this.props.phase === 'RESOURCE_PHASE') {
            if (filling === "empty") {
                //empty squares can be selected for resource placing
                this.props.selectSquare(this.props.index);
            } else { this.props.selectSquare(null) }
        }  else if (this.props.phase === 'BUILDING_PHASE') {
            if (this.props.selectedPawn === null && (filling === "stone" || filling === "brick"|| filling === "wheat" || filling === "glass" || filling === "wood" )) {
                //select resorces to check pattern
                this.props.selectPattern(this.props.index);
            } else if (this.props.selectedPawn) {
                //select square for placing building
                //TODO: add property to build anywhere
                if (this.props.selectedPattern.find(cell => cell === this.props.index)) {
                     this.props.selectSquare(this.props.index);
                    }
            } 
        }
    }

    render() {
        let cellCalss = "cell";
        if (this.props.selectedSquare === this.props.index) { cellCalss += " selectedCell"; }
        if ( this.props.selectedPattern.find(el => el === this.props.index)) { cellCalss+=" patternedCell" }

        return (
            <div className={cellCalss} onClick={() => { this.onCellClick() }}>
                {this.props.cell}
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        cell: state.board[ownProps.index],
        selectedSquare: state.selectedSquare,
        selectedPattern: state.selectedPattern,
        selectedPawn: state.selectedPawn,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { selectSquare, selectPattern })(Square);
