import React, { useEffect } from 'react';
import { connect } from 'react-redux';

import { selectResource } from '../actions';


class Resources extends React.Component {
    render() {
        console.log(this.props);
        return (
            <div>
                Select resource:
                <button onClick={() => { console.log(this.props) }}>Wood</button>
                <button>Wheat</button>
                <button>Brick</button>
                <button>Glass</button>
                <button>Stone</button>
                <button>?</button>
                Selected: {this.props.selectedResource.selectedMaterial}

            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        selectedResource: state.selectedResource
    }
};

export default connect(mapStateToProps, { selectResource })(Resources);
