import React from 'react';
import { connect } from 'react-redux';

//import Grid from './Grid';
import '../css/square.css';
import { selectSquare } from './../actions';

class Square extends React.Component {

    onCellClick(phase) {
        if (this.props.phase === 'RESOURCE_PHASE') {
            if (this.props.cell === "empty") {
                this.props.selectSquare(this.props.index);
            } else { this.props.selectSquare(null) }
        }   
    }

    render() {
        let cellCalss = "cell";
        if (this.props.selectedSquare === this.props.index) { cellCalss += " selectedCell" }

        return (
            <div className={cellCalss} onClick={() => { this.onCellClick() }}>
                {this.props.cell}
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        cell: state.board[ownProps.index],
        selectedSquare: state.board.selectedSquare,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { selectSquare })(Square);
