export  const scoring = board => {

    const indexToX = index => index.toString().charAt(0);
    const indexToY = index => index.toString().charAt(1);
    const xyToIndex = (x,y) =>  `x.toString() + y.toString()`.parseInt();

    const getAdjacentIndexes = index => {
        const x = indexToX(index);
        const y = indexToY(index);
        const adjacent = [];
        if (x > 1) {adjacent.push(xyToIndex(x-1,y) )};
        if (x < 4) {adjacent.push(xyToIndex(x+1,y) )};
        if (y > 1) {adjacent.push(xyToIndex(x,y-1) )};
        if (y > 4) {adjacent.push(xyToIndex(x1,y+1) )};
        return adjacent;
    }

    const findBuildings = buildingTitle => board.indexes.filter(index => board[index].title === buildingTitle);

    const getAdjacentWithType = (index, type) => getAdjacentIndexes(index).filter(index => board[index].type === type)

    const calcWell = (board) => {
        const score = 0;
        findBuildings(5).forEach(wellIndex => { score += getAdjacentWithType(wellIndex, 1).length         
        });

    };

}
