import React from 'react';
import { connect } from 'react-redux';

import { fetchBuilding, changePhaseTo } from '../actions';

class Building extends React.Component {

    selectBuilding = () => {
        
        fetchBuilding("test");
    }
   
    render() {
        return (
            <div>
                <button onClick={() => this.selectBuilding()}>{this.props.title}</button>
            </div>
        );
    }

};

const mapStateToProps = (state) => {
    return {
        selectedPawn: state.selectedPawn,
        selectedSquare: state.selectedSquare,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { fetchBuilding, changePhaseTo })(Building);
