import React from 'react';
import { connect } from 'react-redux';

import { selectResource, changePhaseTo } from '../actions';
import {
    WOOD,
    STONE,
    BRICK,
    WHEAT,
    GLASS,
} from './constants'


class Resources extends React.Component {
    /*
    resourceSelection = resource => {
        if (this.props.phase === "RESOURCE_PHASE") {
            this.props.selectResource(resource);

        }
    }
    */

    render() {
        return (
            <div>
                Select resource:
                <button onClick={() => { this.props.selectResource(WOOD, this.props.phase) }}>Wood</button>
                <button onClick={() => { this.props.selectResource(WHEAT, this.props.phase) }}>Wheat</button>
                <button onClick={() => { this.props.selectResource(BRICK, this.props.phase) }}>Brick</button>
                <button onClick={() => { this.props.selectResource(GLASS, this.props.phase) }}>Glass</button>
                <button onClick={() => { this.props.selectResource(STONE, this.props.phase) }}>Stone</button>
                {this.props.selectedPawn}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        selectedPawn: state.selectedPawn,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { selectResource, changePhaseTo })(Resources);
