import React from 'react';
import { connect } from 'react-redux';

import { fetchBuilding, selectPawn, changeMessage } from '../../actions';
import data from './buildings.json';
import { message, BUILDING_PHASE, SCORING_PHASE } from './../constants';
import { calcScore } from './scoring';



class Building extends React.Component {

    testMe() {
        const normalaized = arr => {
            const dx = Math.min(...arr.map(cell => Number(cell.x))) - 1;
            const dy = Math.min(...arr.map(cell => Number(cell.y))) - 1;
            return arr.map(cell => {
                return {
                    x: cell.x - dx,
                    y: cell.y - dy,
                    //resource: cell.resource

                }
            });
        }

        const rotate = arr => {
            return arr.map(point => { return { x: -point.y, y: point.x } });
        }

        const mirrorX = arr => {
            return arr.map(point => { return { x: -point.x, y: point.y } });

        }

        const mirrorY = arr => {
            return arr.map(point => { return { x: point.x, y: -point.y } });

        }
        console.log(this.props.building.pattern);

        console.log(normalaized(rotate(this.props.building.pattern)));

        console.log(mirrorX(this.props.building.pattern));
        console.log(mirrorY(this.props.building.pattern));

    }

    selectBuilding() {
        if (this.props.phase === BUILDING_PHASE) {
            if (this.props.selectedPattern.length && "Pattern is OK") {
                this.props.selectPawn(this.props.building);

            }
            this.props.changeMessage(message.selectSquareForBuilding(this.props.building.title, this.props.selectedPattern.length));
        }
    }

    componentDidMount() {
        this.props.fetchBuilding(data[this.props.title]);
    }

    renderScoring() {
        if (this.props.phase === SCORING_PHASE) {
            return calcScore[this.props.building.title](this.props.board)
        };
    }

    render() {
        if (!this.props.building) {
            return null;
        } else {
            return (
                <div>
                    <button onClick={() => this.selectBuilding()}>{this.props.building.title}</button>
                    {this.renderScoring()}
                    <button onClick={() => this.testMe()}> testMe </button>
                </div>
            );
        }
    }

};

const mapStateToProps = (state, ownProps) => {
    return {
        phase: state.phase,
        building: state.buildings[ownProps.index],
        selectedPattern: state.selectedPattern,
        board: state.board
    }
};

export default connect(mapStateToProps, { fetchBuilding, selectPawn, changeMessage })(Building);
