import {
    SELECT_RESOURCE,
    PLACE_RESOURCE,
    CHANGE_OF_PHASE_TO
} from '../actions/types'


const INITIAL_STATE = {
    phase: "PHASE_CHOOSING_RESOURCE",
    firstPlayer: "player",
    selectedResource: null,
    selectedBuilding: null,

};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case CHANGE_OF_PHASE_TO:
            return { ...state, phase: action.payload };
        case CHANGE_PLAYER_TO:
            return { ...state, firstPlayer: action.payload };
        case SELECT_RESOURCE:
            return { ...state, selectedResource: action.payload };
        case PLACE_RESOURCE:
            return { ...state, selectedResource: null };
        default:
            return state;
    }
};



