import {
    SELECT_RESOURCE,
    CHANGE_OF_PHASE_TO
    
} from './types';


export const selectResource = resource => {
    return {
        type: SELECT_RESOURCE,
        payload: resource
    };
}

//phases: "RESOURCE_PHASE", "BUILDING_PHASE",
export const changePhaseTo = phase => {
    return {
        type: CHANGE_OF_PHASE_TO,
        payload: phase
    };
}

export const changePlayerTo = player => {
    return {
        type: CHANGE_PLAYER_TO,
        payload: player
    };
}
