import React from 'react';
import { connect } from 'react-redux';

import { fetchBuilding, selectPawn, changeMessage } from '../../actions';
import data from './buildings.json';
import { BUILDING_PHASE } from './../constants';
import { messageSelectSquareForBuilding } from './../messages';



class Building extends React.Component {

    selectBuilding() {
        if (this.props.phase === BUILDING_PHASE) {
            if (this.props.selectedPattern !== [] && "Pattern is OK") {
                console.log(this.props.selectedPattern !== []);
                this.props.selectPawn(this.props.building.title);

            }
            this.props.changeMessage(messageSelectSquareForBuilding(this.props.building.title, this.props.selectedPattern === []));
        }
    }

    componentDidMount() {
        this.props.fetchBuilding(data[this.props.title]);
    }

    render() {
        if (!this.props.building) {
            return null;
        } else {
            return (
                <div>
                    <button onClick={() => this.selectBuilding()}>{this.props.building.title}</button>
                </div>
            );
        }
    }

};

const mapStateToProps = (state, ownProps) => {
    return {
        phase: state.phase,
        building: state.buildings[ownProps.type],
        selectedPattern: state.selectedPattern
    }
};

export default connect(mapStateToProps, { fetchBuilding, selectPawn, changeMessage })(Building);
