import React from 'react';
import { connect } from 'react-redux';

import { selectResource, changePhaseTo } from '../actions';
import {
    EMPTY_SQUARE,
    WOOD_ON_SQUARE,
    STONE_ON_SQUARE,
    BRICK_ON_SQUARE,
    WHEAT_ON_SQUARE,
    GLASS_ON_SQUARE,
} from './constants'


class Resources extends React.Component {
    /*
    resourceSelection = resource => {
        if (this.props.phase === "RESOURCE_PHASE") {
            this.props.selectResource(resource);

        }
    }
    */

    render() {
        return (
            <div>
                Select resource:
                <button onClick={() => { this.props.selectResource(WOOD_ON_SQUARE, this.props.phase) }}>Wood</button>
                <button onClick={() => { this.props.selectResource(WHEAT_ON_SQUARE, this.props.phase) }}>Wheat</button>
                <button onClick={() => { this.props.selectResource(BRICK_ON_SQUARE, this.props.phase) }}>Brick</button>
                <button onClick={() => { this.props.selectResource(GLASS_ON_SQUARE, this.props.phase) }}>Glass</button>
                <button onClick={() => { this.props.selectResource(STONE_ON_SQUARE, this.props.phase) }}>Stone</button>
                {this.props.selectedPawn}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        selectedPawn: state.selectedPawn,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { selectResource, changePhaseTo })(Resources);
