import React from 'react';
import { connect } from 'react-redux';

import { selectPawn } from '../actions';

const ResourceBtn = props => {
    
    resourceSelection = resource => {
        //select resource
        this.props.clearSelection();
        this.props.selectPawn(resource);
        this.props.changeMessage(message.placeResource(resource.title));

    }

    return <button
            key={props.resource.title}
            onClick={() => { this.resourceSelection(props.resource) }}
            className={`${props.resource.title.toLowerCase()} resourceBtn`}
        ></button>
};



export default connect(null, { selectPawn })(ResourceBtn);
