import {
    SELECT_RESOURCE,
    SELECT_SQUARE,
    PLACE_RESOURCE,
    CHANGE_PHASE

} from '../actions/types'

const INITIAL_SQUARE =      "empty"; //'wheet', 'wood'..., "a1", "b2","c2"...


const INITIAL_BOARD = {
    selectedSquare: null,
    toggleRecource: null,
    11:  "BLOCK",
    12: INITIAL_SQUARE,
    13: INITIAL_SQUARE,
    14: INITIAL_SQUARE,
    21: INITIAL_SQUARE,
    22: INITIAL_SQUARE,
    23: INITIAL_SQUARE,
    24: INITIAL_SQUARE,
    31: INITIAL_SQUARE,
    32: INITIAL_SQUARE,
    33: INITIAL_SQUARE,
    34: INITIAL_SQUARE,
    41: INITIAL_SQUARE,
    42: INITIAL_SQUARE,
    43: INITIAL_SQUARE,
    44: INITIAL_SQUARE
};


const INITIAL_STATE = {
    phase: "RESOURCE_PHASE",
    selectedPawn: null,
    board: INITIAL_BOARD

};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {

        case CHANGE_PHASE:
            return { ...state, phase: action.payload };

        case SELECT_RESOURCE:
            return { ...state, selectedPawn: action.payload };


        case SELECT_SQUARE:
            //state.board.selectedSquare = action.payload;
            return { ...state, 'board': { ...state.board, selectedSquare:action.payload } };


        //TODO update the grid!!!
        case PLACE_RESOURCE:
            return { ...state, 'board': { ...state.board, [action.payload]: state.selectedPawn }, selectedPawn: null,  };

        default:
            return state;
    }
};



