import { SELECT_RESOURCE, PLACE_RESOURCE } from '../actions/types'


const INITIAL_STATE = { selectedMaterial: null };

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case SELECT_RESOURCE:
            return { ...state, selectedMaterial:action.payload };
        case PLACE_RESOURCE:
            return { ...state, selectedMaterial:null };
        default:
            return state;
    }
};
