import { messageSelectResource } from './../components/messages';

//phases
export const RESOURCE_PHASE = "RESOURCE_PHASE";
export const BUILDING_PHASE = "BUILDING_PHASE";
export const SCORING_PHASE = "SCORING_PHASE";

//square's occupants
export const EMPTY_SQUARE = { title: "EMPTY_SQUARE", type: "EMPTY" };
export const RESOURCE = "RESOURCE";//= [WOOD, STONE, BRICK, WHEAT, GLASS];
export const WOOD =  { title: "WOOD", type: RESOURCE };
export const STONE = { title: "STONE", type: RESOURCE };
export const BRICK = { title: "BRICK", type: RESOURCE };
export const WHEAT = { title: "WHEAT", type: RESOURCE };
export const GLASS = { title: "GLASS", type: RESOURCE };

const INITIAL_BOARD = {
    indexes: [
        11,
        12,
        13,
        14,
        21,
        22,
        23,
        24,
        31,
        32,
        33,
        34,
        41,
        42,
        43,
        44
    ]
}

INITIAL_BOARD.indexes.forEach(index => {
    INITIAL_BOARD[index] = EMPTY_SQUARE;
});
INITIAL_BOARD[11] = EMPTY_SQUARE;

export const INITIAL_STATE = {
    phase: RESOURCE_PHASE,
    // phase: BUILDING_PHASE,
    selectedPawn: null,
    selectedSquare: null,
    selectedPattern: [],
    buildings: [],
    board: INITIAL_BOARD,
    message: messageSelectResource()
};
