import React from 'react';
import { connect } from 'react-redux';


import Resource from './Resource';
import { changePhaseTo, placeResource, clearSelection } from '../actions';

import {
    message,
    RESOURCE_PHASE,
    BUILDING_PHASE
} from './constants';
import '../css/resources.css';

const placeSelectedResource = props => {
    props.placeResource();
    props.clearSelection();
    props.changePhaseTo(BUILDING_PHASE);
;}

const renderActionHelper = props => {
    if (props.phase === RESOURCE_PHASE) {
        if (!props.selectedPawn.type && !props.selectedSquare) {
            return message.selectResourceAndPlace;
        } else if (!props.selectedPawn.type) {
            return message.selectResource;

        } else if (!props.selectedSquare) {
            return message.placeResource(props.selectedPawn.title);
        } else {
            return <button onClick={() => placeSelectedResource(props)}> {message.confirmResourcePlacement(props.selectedPawn.title)} </button>
        }

    } else {
        return null;
    }
}

const ResourceSelector = props => {
    return (
        <div className="resourceSelector">
            Select resource
            <Resource id={0} />
            <Resource id={1} />
            <Resource id={2} />
            {renderActionHelper(props)}
        </div>
    );
};

const mapStateToProps = state => {
    return {
        selectedPawn: state.selectedPawn,
        selectedSquare: state.selectedSquare,
        phase: state.phase
    }
};

export default connect(mapStateToProps, {changePhaseTo, placeResource, clearSelection})(ResourceSelector);




