import React from 'react';
import { connect } from 'react-redux';

import { selectResource, changePhaseTo, changePlayerTo } from '../actions';

const Buttons = () => {
    const nextRound = () => {
        const nextFirstPlayer = this.props.firstPlayer === "player" ? "random" : "player";
            this.props.changePlayerTo(nextFirstPlayer);
    }


    return (
        <div> 
            <button>Place</button>
            <button>NEXT</button> 
        </div>
    );

};




const mapStateToProps = (state) => {
    return {
        selectedResource: state.selectedResource,
        phase: state.phase,
        firstPlayer: state.firstPlayer
    }
};

export default connect(mapStateToProps, { selectResource, changePhaseTo, changePlayerTo })(Buttons);
