import {
    SELECT_RESOURCE,
    SELECT_SQUARE,
    SELECT_PATTERN,
    CLEAR_SELECTION,
    PLACE_RESOURCE,
    FETCH_BUILDING,
    CHANGE_PHASE

} from '../actions/types'

const INITIAL_SQUARE = "empty"; //'wheet', 'wood'..., "a1", "b2","c2"...


const INITIAL_BOARD = {

    11: "BLOCK",
    12: "wheat",
    13: INITIAL_SQUARE,
    14: INITIAL_SQUARE,
    21: INITIAL_SQUARE,
    22: INITIAL_SQUARE,
    23: INITIAL_SQUARE,
    24: INITIAL_SQUARE,
    31: INITIAL_SQUARE,
    32: INITIAL_SQUARE,
    33: INITIAL_SQUARE,
    34: INITIAL_SQUARE,
    41: INITIAL_SQUARE,
    42: INITIAL_SQUARE,
    43: INITIAL_SQUARE,
    44: INITIAL_SQUARE
};


const INITIAL_STATE = {
    //phase: "RESOURCE_PHASE",
    phase: "BUILDING_PHASE",
    selectedPawn: null,
    selectedSquare: null,
    selectedPattern: [],
    buildings: [],
    board: INITIAL_BOARD
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {

        case CHANGE_PHASE:
            return { ...state, phase: action.payload };

        case SELECT_RESOURCE:
            return { ...state, selectedPawn: action.payload };

        case SELECT_SQUARE:
            //state.board.selectedSquare = action.payload;
            return { ...state, selectedSquare: action.payload };

        case SELECT_PATTERN:
            if (state.selectedPattern.find(el => el === action.payload)) {
                return { ...state, selectedPattern: state.selectedPattern.filter(el => el !== action.payload) };
            }
            return { ...state, selectedPattern: [...state.selectedPattern, action.payload] };

        case CLEAR_SELECTION:
            //state.board.selectedSquare = action.payload;
            return { ...state, selectedSquare: null, selectedPattern: [], selectedPawn: null };

        case FETCH_BUILDING:
            console.log( action.payload);
            return { ...state, buildings: [ ...state.buildings, action.payload ] };


        case PLACE_RESOURCE:
            return { ...state, board: { ...state.board, [state.selectedSquare]: state.selectedPawn }, selectedSquare: null, selectedPawn: null };

        default:
            return state;
    }
};



