import {
    SELECT_RESOURCE,
    PLACE_RESOURCE,
    CHANGE_PHASE
    
} from '../actions/types'


const INITIAL_STATE = {
    phase: "RESOURCE_PHASE",
    selectedPawn: null,
    board: {}

};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {

        case CHANGE_PHASE:
            return { ...state, phase: action.payload };
        
        case SELECT_RESOURCE:
            return { ...state, selectedPawn: action.payload };

            //TODO update the grid!!!
        case PLACE_RESOURCE:
            return { ...state, selectedPawn: null };

        default:
            return state;
    }
};



