export const messageSelectResource = "Select a resource";
export const messagePlaceResource = resource => `Place the ${resource} or change it`;
export const messageConfirmResourcePlacement = resource => `Confirm placement ${resource} on chosen square or select another resource/square`;
export const messageShowPattern = () => "Select resources for building constraction. Then select the building for pattern check. Or start a new round.";
export const messageSelectSquareForBuilding = (building, canBePlaced) => canBePlaced ? `Select a square for the ${building} placement. All corresponding resources will be removed. Or start a new round.` : `Select  ${building} pattern. Or start a new round.`;
