export const message = {
    placeResource: resource => `Place the ${resource} or change it`,
    confirmResourcePlacement: resource => `Confirm placement ${resource} on chosen square or select another resource/square`,
    selectSquareForBuilding: (building, canBePlaced) => canBePlaced ? `Select a square for the ${building} placement. All corresponding resources will be removed. Or start a new round.` : `Select  ${building} pattern. Or start a new round.`,
    selectResource: "Select a resource",
    showPattern: "Select resources for building constraction. Then select the building for pattern check. Or start a new round.",
}

//phases
export const RESOURCE_PHASE = "RESOURCE_PHASE";
export const BUILDING_PHASE = "BUILDING_PHASE";
export const FEEDING_PHASE = "FEEDING_PHASE";
export const SCORING_PHASE = "SCORING_PHASE";

//square's occupants
export const EMPTY_SQUARE = { title: "EMPTY", type: "EMPTY" };
export const RESOURCE = "RESOURCE";
export const WOOD = { title: "WOOD", type: RESOURCE };
export const STONE = { title: "STONE", type: RESOURCE };
export const BRICK = { title: "BRICK", type: RESOURCE };
export const WHEAT = { title: "WHEAT", type: RESOURCE };
export const GLASS = { title: "GLASS", type: RESOURCE };

export const buildingsForFirstPlay = ["Cottage", "Chapel", "Farm", "Tavern", "Well", "Theatr", "Factory"];

const INITIAL_BOARD = {
    indexes: [11, 12, 13, 14, 21, 22, 23, 24, 31, 32, 33, 34, 41, 42, 43, 44],
    fedCottages: 0
}
INITIAL_BOARD.indexes.forEach(index => {
    INITIAL_BOARD[index] = WOOD;
});
//INITIAL_BOARD[11] = EMPTY_SQUARE;

/*function shuffle(array) {
    var currentIndex = array.length, temporaryValue, randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

        // Pick a remaining element...
        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex -= 1;

        // And swap it with the current element.
        temporaryValue = array[currentIndex];
        array[currentIndex] = array[randomIndex];
        array[randomIndex] = temporaryValue;
    }

    return array;
}*/

const shuffle = () => {
    let cards = [WOOD, STONE, BRICK, WHEAT, GLASS];
    cards = [...cards, ...cards, ...cards];

    return cards;



}

export const INITIAL_STATE = {
    cards: shuffle(),
    phase: BUILDING_PHASE,
    selectedPawn: {},
    selectedSquare: null,
    selectedPattern: [],
    buildings: [],
    board: INITIAL_BOARD,
    message: message.selectResource
};
