import React from 'react';
import { connect } from 'react-redux';

import { selectResource, changePhaseTo, changePossibleAction } from '../actions';
import {
    RESOURCE_PHASE,
    WOOD,
    STONE,
    BRICK,
    WHEAT,
    GLASS,
} from './constants';
import  { messagePlaceResource } from './messages';


class Resources extends React.Component {
    
    resourceSelection = resource => {
        if (this.props.phase === RESOURCE_PHASE) {
            this.props.selectResource(resource);
            this.props.changePossibleAction(messagePlaceResource(resource));
        }
    }
    

    render() {
        return (
            <div>
                Select resource:
                <button onClick={() => { this.props.selectResource(WOOD, this.props.phase); 
                    this.props.changePossibleAction(messagePlaceResource(WOOD) ) }}>Wood</button>
                <button onClick={() => { this.props.resourceSelection(WHEAT) }}>Wheat</button>
                <button onClick={() => { this.props.resourceSelection(BRICK) }}>Brick</button>
                <button onClick={() => { this.props.resourceSelection(GLASS) }}>Glass</button>
                <button onClick={() => { this.props.resourceSelection(STONE) }}>Stone</button>
                {this.props.selectedPawn}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        selectedPawn: state.selectedPawn,
        phase: state.phase
    }
};

export default connect(mapStateToProps, { selectResource, changePhaseTo, changePossibleAction })(Resources);
