import {
    BUILDING_PHASE,
    EMPTY_SQUARE
} from './../components/constants';

import {
    SELECT_PAWN,
    SELECT_RESOURCE,
    SELECT_SQUARE,
    SELECT_PATTERN,
    CLEAR_SELECTION,
    PLACE_RESOURCE,
    PLACE_BUILDING,
    FETCH_BUILDING,
    CHANGE_PHASE

} from '../actions/types'

const INITIAL_BOARD = {
    indexes: [11, 12, 13, 14, 21, 22, 23, 24, 31, 32, 33, 34, 41, 42, 43, 44]
}

INITIAL_BOARD.indexes.forEach(index => {
    INITIAL_BOARD[index] = EMPTY_SQUARE;
});
//  11: "BLOCK",
///  12: "wheat",

const INITIAL_STATE = {
    //phase: "RESOURCE_PHASE",
    phase: BUILDING_PHASE,
    selectedPawn: null,
    selectedSquare: null,
    selectedPattern: [],
    buildings: [],
    board: INITIAL_BOARD,
    possibleActions: "Select "
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {

        case CHANGE_PHASE:
            return { ...state, phase: action.payload };

        case SELECT_RESOURCE:
        case SELECT_PAWN:
            return { ...state, selectedPawn: action.payload };

        case SELECT_SQUARE:
            //state.board.selectedSquare = action.payload;
            return { ...state, selectedSquare: action.payload };

        case SELECT_PATTERN:
            if (state.selectedPattern.find(el => el === action.payload)) {
                return { ...state, selectedPattern: state.selectedPattern.filter(el => el !== action.payload) };
            }
            return { ...state, selectedPattern: [...state.selectedPattern, action.payload] };

        case CLEAR_SELECTION:
            //state.board.selectedSquare = action.payload;
            return { ...state, selectedSquare: null, selectedPattern: [], selectedPawn: null };

        case FETCH_BUILDING:
            return { ...state, buildings: [...state.buildings, action.payload] };


        case PLACE_RESOURCE:
            return { ...state, board: { ...state.board, [state.selectedSquare]: state.selectedPawn }, selectedSquare: null, selectedPawn: null };

        case PLACE_BUILDING:
            state.selectedPattern.forEach(cell => {
                state.board[cell] = EMPTY_SQUARE;
            })
            return { ...state, board: { ...state.board, [state.selectedSquare]: state.selectedPawn }, selectedSquare: null, selectedPawn: null, selectedPattern: [] };

        default:
            return state;
    }
};



