export  const scoring =  {

     indexToX: index => index.toString().charAt(0),
     indexToY: index => index.toString().charAt(1),
     xyToIndex: (x,y) =>  parseInt(x.toString() + y.toString()),

     getAdjacentIndexes: index => {
        const x = this.indexToX(index);
        const y = this.indexToY(index);
        const adjacent = [];
        if (x > 1) {adjacent.push(this.xyToIndex(x-1,y) )};
        if (x < 4) {adjacent.push(this.xyToIndex(x+1,y) )};
        if (y > 1) {adjacent.push(this.xyToIndex(x,y-1) )};
        if (y > 4) {adjacent.push(this.xyToIndex(x,y+1) )};
        return adjacent;
    },

     findBuildings: (board, buildingTitle) => board.indexes.filter(index => board[index].title === buildingTitle),

     getAdjacentWithType: (board, index, type) => this.getAdjacentIndexes(index).filter(index => board[index].type === type),

     calcWell: (board) => {
        let score = 0;
        this.findBuildings(board, 5).forEach(wellIndex => { score += this.getAdjacentWithType(board, wellIndex, 1).length         
        });
        return score;
    },

}
