import React from 'react';
import { connect } from 'react-redux';

import preparedPattern from './preparedPattern';

//import { selectResource, changePhaseTo, placeResource, clearSelection } from '../actions';

const PatternDetector = ({ selectedPattern, board }) => {
    const showPattern = (pattern) => {      
        console.log( preparedPattern(pattern));
    }

    return (
        <div>
            <button onClick={() => showPattern(selectedPattern)}>Show Pattern</button>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        selectedPattern: state.selectedPattern,
        board: state.board

    }
};

export default connect(mapStateToProps, {})(PatternDetector);
