import {
    SELECT_RESOURCE,
    SELECT_SQUARE,
    SELECT_PATTERN,
    CLEAR_SELECTION,
    PLACE_RESOURCE,
    CHANGE_PHASE

} from './types';


export const selectResource = (resource, phase) => {
    if (phase === 'RESOURCE_PHASE') {
        return {
            type: SELECT_RESOURCE,
            payload: resource
        };
    }
    return { type: null };
}

export const selectSquare = (index) => {
    return {
        type: SELECT_SQUARE,
        payload: index
    };
}

export const selectPattern = (index) => {
    return {
        type: SELECT_PATTERN,
        payload: index
    };
}

export const clearSelection = (index) => {
    return {
        type: CLEAR_SELECTION
    };
}

export const placeResource = () => {
    return {
        type: PLACE_RESOURCE
    };
}


//phases: "RESOURCE_PHASE", "BUILDING_PHASE",
export const changePhaseTo = phase => {
    return {
        type: CHANGE_PHASE,
        payload: phase
    };
}
