import React from 'react';
import { connect } from 'react-redux';

import { fetchBuilding, selectPawn } from '../../actions';
import data from './buildings.json';


class Building extends React.Component {

    selectBuilding() {
        this.props.selectPawn(this.props.building.title);
    }
    
    componentDidMount() {
        this.props.fetchBuilding(data[this.props.title]);
    }

    render() {
        if (!this.props.building) {
            return null;
        } else {
            return (
                <div>
                    <button onClick={() => this.selectBuilding()}>{this.props.building.title}</button>
                </div>
            );
        }
    }

};

const mapStateToProps = (state, ownProps) => {
    return {

        building: state.buildings[ownProps.type]
    }
};

export default connect(mapStateToProps, { fetchBuilding })(Building);
