import {
    SELECT_RESOURCE,
    CHANGE_PHASE
    
} from './types';


export const selectResource = (resource, phase) => {
    if (phase === 'RESOURCE_PHASE') {
    return {
        type: SELECT_RESOURCE,
        payload: resource
    };
}
}

//phases: "RESOURCE_PHASE", "BUILDING_PHASE",
export const changePhaseTo = phase => {
    return {
        type: CHANGE_PHASE,
        payload: phase
    };
}
